{
    "openapi": "3.0.0",
    "info": {
        "title": "NLW#5",
        "description": "Chat using sockets",
        "version": "1.0",
        "contact": {
            "email": "rodrigomendoncca@gmail.com"
        }
    },
    "tags": [
        {
            "name": "Session",
            "description": "Requests about session initialize end terminating"
        },
        {
            "name": "chat",
            "description": "Requests for chat functionality"
        }
    ],
    "servers": [
        {
            "url": "http://rodchat.herokuapp.com/",
            "description": "Production server"
        },
        {
            "url": "http://localhost:3333/",
            "description": "Development server"
        }
    ],
    "paths": {
        "/settings": {
            "post": {
                "summary": "Create a Chat settings",
                "tags": [
                    "chat"
                ],
                "parameters": [
                  {
                    "name": "username",
                    "in": "body",
                    "description": "The chat name",
                    "required": true,
                    "example": {
                      "username": "SomeChat"
                    },
                    "style": "simple"
                  },
                  {
                    "name": "chat",
                    "in": "body",
                    "description": "a boolean defining the chat properties. `DEFAULT: true`",
                    "required": true,
                    "example": {
                      "chat": true
                    },
                    "schema": {
                      "type": "boolean",
                      "items": {
                        "format": "boolean"
                      }                      
                    },
                    "style": "simple"
                  },
                ],

            }
        }
    },
    "components": {
        "schemas": {
            "userToken": {
                "type": "string",
                "description": "The User jwt token",
                "example": {
                    "User-Token": "lno7nlpds3imacrqhshpunumm0"
                },
                "properties": {
                    "session_token": {
                        "type": "string",
                        "description": "The user token for authorizing theuse of the API"
                    }
                }
            }
        }
    }

}
